import React from 'react';
import CalendarSection from './CalendarSection';


export default function AppointManagement(props) {
  const { appointments } = props;

  // JSX
  return (
    <div id="appoint-mgt-container">
      <p>{props.appointments.length} appointments scheduled for today.</p>
      <p>Click on an appointment to view or edit details.</p>
      
      {/* Render appointments */}
      <div id="appoint-list-container">
        {props.appointments.map((appointment, index) => (
          <div key={index} className="appointment-card">
            <h2>{appointment.name}</h2>
            <p>{appointment.date}</p>
            <p>{appointment.time}</p>
            <button onClick={() => props.handleEditAppointment(index)}>Edit</button>
          </div>
        ))}
      </div>

      <form id="appoint-input-form">
        <input type="text" id="name" placeholder="Requester Name" /><br/>
        <input type="text" id="title" placeholder="Appointment Title" /><br/>
        <input type="date" id="date" /><br/>
        <button onClick={props.handleAddAppointment}>Add Appointment</button>
      </form>
      <hr />

      <CalendarSection appoints={appointments}/>
    </div>
  );
}
