import React from 'react';



export default function TaskList(props) {
  const {tasks, handleDeleteTask, handleEditTask} = props;

  console.log(tasks, " in TaskList()");

  // JSX
  return (
    <div id="tasklist-container">
      {tasks.length < 1 ? 
        <p>No tasks at this moment...</p> 
        : 
        tasks.map((task, id) => 
          <div key={id}>
            <p>ID: {task.id}</p>
            <p>TITLE: {task.title}</p>
            <p>DESCRIPTION: {task.details}</p>
            <button onClick={() => handleDeleteTask(id)}>Delete</button>
            <button onClick={() => handleEditTask(id)}>Edit</button>
          </div>)
      }
    </div>
  );
}
