import React from 'react';



export default function TaskList(props) {
  const {tasks} = props;


  // JSX
  return (
    <div id="tasklist-container">
      {tasks.map((task, id) => <p key="id">{task.title}: {task.details}</p>)}
    </div>
  );
}
