import React from 'react';



export default function TaskList(props) {
  const {tasks, remDays, handleDeleteTask, handleEditTask, handleTaskDelete, handleEditTaskClick} = props;

  console.log(tasks, " in TaskList()");
  console.log("Remaining Days: ", remDays);

  // JSX
  return (
    <div id="tasklist-container">
      <ul is="tasklist">
      {tasks.length < 1 ? 
        <p>No tasks at this moment...</p> 
        : 
        tasks.map((task, id) => 
          <li key={id}>
            <p>ID: {task.id}</p>
            <p>TITLE: {task.title}</p>
            <p>DESCRIPTION: {task.details}</p>
            <p>STATUS: {task.status}</p>
            <p>PRIORITY: {task.priority}</p>
            <p>DUE DATE: {task.dueDate}</p>
            <p>REMAINING TIME: {remDays}</p>
            <button onClick={() => handleTaskDelete(task.id)}>Delete</button>
            <button onClick={() => handleEditTaskClick(task.id)}>Edit</button>
          </li>)
      }
      </ul>
    </div>
  );
}
