import React from 'react';
import TaskList from './TaskList';



export default function TaskManagement(props) {
  const {
    newTask, 
    tasks, 
    handleSubmitTask, 
    handleNewTaskChange,
    handleTaskDelete,
    handleEditTaskClick
  } = props;



  // JSX
  return (
    <div id="task-management-container">

      <form onSubmit={handleSubmitTask}>
        <label>Task Title: </label>
        <input 
          type='text' 
          name="title" 
          value={newTask.title || ""} 
          onChange={handleNewTaskChange} 
          placeholder="Add a task title" 
        />
        <br />
        <label>Task Details: </label>
        <textarea
          type="text" 
          name="details" 
          value={newTask.details || ""} 
          onChange={handleNewTaskChange} 
          placeholder="Add a task details" 
        />
        <br />
        <button type="submit">Add Task</button>
      </form>

      {/* For now disable those functions below */}
      <div id="taskmgt-btn-container">
        {/* <button onClick={clearTasks}>Clear Tasks</button>
        <button onClick={deleteAllTasks}>Delete All Tasks</button>
        <button onClick={toggleCompletedTasks}>Toggle Completed Tasks</button>
        <button onClick={sortTasks}>Sort Tasks</button>
        <button onClick={filterTasks}>Filter Tasks</button>
        <button onClick={searchTasks}>Search Tasks</button>
        <button onClick={undoLastTask}>Undo Last Task</button> */}
      </div>

      <TaskList 
        tasks={tasks}
        handleTaskDelete={handleTaskDelete}
        handleEditTaskClick={handleEditTaskClick} 
      />

    </div>
  );
}
