import React, { useState } from 'react';
import AppointManagement from './component/AppointManagement';
import TaskManagement from './component/TaskManagement';




function App() {
  // page determines which app opens now. 1 = task management app, 2 = appointments app
  const [page, setPage] = useState(1);
  const [tasks, setTasks] = useState([]);
  const [appointments, setAppointments] = useState([]);


  // Page Control
  const handlePageClick = (pageNumber) => {
    setPage(pageNumber);
  };



  // Task Management
  const [newTask, setNewTask] = useState({});
  // FUNCTION: get use input
  const handleNewTaskChange = (e) => {
    const {name, value} = e.target;

    setNewTask((prev) => ({...prev, id: Date.now(), [name]: value}));
    console.log(name, ", ", value, " in handleNewTaskChange");
  };

  const handleSubmitTask = (e) => {
    e.preventDefault();

    setTasks((prev) => [...prev, newTask]);
    setNewTask({});
    // alert("Form Submitted");
  };

  const handleTaskDelete = (targetId) => {
    setTasks(tasks.filter((task) => task.id !== targetId));
  };

  const handleEditTaskClick = (targetId) => {
    // 1. find the target task
    // 2. display the current contents on the input form
    // 3. update as users want
    // 4. submit the task and it should be work as usual like the newly adding a task case
    // Actual roles for this function are the fist 2 items above...
    console.log("targetId of task you clicked is: ", targetId);
    const targetTask = tasks.find((task) => task.id === task.target.id);
    if(!targetTask) {
      // This shouldn't occur as normal.  So, this case is an error case.
      console.error("Couldn't find the target task for some error...");
      return;
    }

    // Display the task found in the form
    setNewTask(targetTask);
  };




  // JSX
  return (
    <div className="App">
      <div id="APP-header">
        {page===1 ? 
          <div>
            <h1>Task Management App</h1>
            <p onClick={() => handlePageClick(2)}>Appointment Management App</p>
          </div> 
          :
         page===2 ? 
          <div>
            <h1>Appointment Management App"</h1>
            <p onClick={() => handlePageClick(1)}>Task Management App</p> 
          </div>
          : 
         <p>Error</p>}
      </div>

      <div id="App-main">
        {page === 1 ? 
          <TaskManagement 
            newTask={newTask}
            tasks={tasks} 
            handleSubmitTask={handleSubmitTask}
            handleNewTaskChange={handleNewTaskChange}
            handleTaskDelete={handleTaskDelete}
            handleEditTaskClick={handleEditTaskClick}
          /> 
          : 
          null}
        {page === 2 ? <AppointManagement appointments={appointments} /> : null}
      </div>

      <div id="App-footer">
        <p>Test Outputs:</p>
        <p>Current Task Title Input: {newTask.title}</p>
      </div>
    </div>
  );
}

export default App;
