import React from 'react';



export default function AppointManagement(props) {
  

  // JSX
  return (
    <div id="appoint-management-container">
      <h1>Appoint Management</h1>
      <p>
        {props.appointments.length} appointments scheduled for today.
        Click on an appointment to view or edit details.
      </p>
      {/* Render appointments */}
      {props.appointments.map((appointment, index) => (
        <div key={index} className="appointment-card">
          <h2>{appointment.name}</h2>
          <p>{appointment.date}</p>
          <p>{appointment.time}</p>
          <button onClick={() => props.handleEditAppointment(index)}>Edit</button>
        </div>
      ))}
      <button onClick={props.handleAddAppointment}>Add New Appointment</button>
    </div>
  );
}
