import React from 'react';



export default function TaskList(props) {
  const {tasks, remDays, handleDeleteTask, handleEditTask, handleTaskDelete, handleEditTaskClick} = props;

  console.log(tasks, " in TaskList()");
  console.log("Remaining Days: ", remDays);

  // JSX
  return (
    <div id="tasklist-container">
      <h2 id="tasklist-title">TASK LIST</h2>
      <ul id="tasklist">
      {tasks.length < 1 ? 
        <p>No tasks at this moment...</p> 
        : 
        tasks.map((task, id) => 
          <li id="tasklist-task" key={id}>
            <p id="tasklist-task-id"><span id="col1">Task ID:</span> <span id="col2">{task.id}</span></p>
            <p id="tasklist-task-title">TITLE: {task.title}</p>
            <p id="tasklist-task-description">DESCRIPTION: {task.details}</p>
            <p id="tasklist-task-status">STATUS: {task.status}</p>
            <p id="tasklist-task-priority">PRIORITY: {task.priority}</p>
            <p id="tasklist-task-due">DUE DATE: {task.dueDate}</p>
            <p id="tasklist-task-remaining">REMAINING Days: {remDays} days</p>
            <button id="tasklist-task-delete-btn" className="tasklist-btn" onClick={() => handleTaskDelete(task.id)}>Delete</button>
            <button id="tasklist-task-edit-btn" className="tasklist-btn" onClick={() => handleEditTaskClick(task.id)}>Edit</button>
          </li>)
      }
      </ul>
    </div>
  );
}
