import React from 'react';



export default function TaskList(props) {
  const {tasks} = props;

  console.log(tasks, " in TaskList");
  
  console.log("CHECK if tasks can be other than array type: ", tasks);
  if(!Array.isArray(tasks)) {
    console.log("non-array tasks: ", tasks);
    return;
  }
  console.log('"tasks" is an array!');



  // JSX
  return (
    <div id="tasklist-container">
      {tasks.length < 1 ? 
        <p>No tasks at this moment...</p> 
        : 
        tasks.map((task, id) => <p key={id}>{task.title}</p>)
      }
    </div>
  );
}
