import React, { useState } from 'react';
import AppointManagement from './component/AppointManagement';
import TaskManagement from './component/TaskManagement';




function App() {
  // page determines which app opens now. 1 = task management app, 2 = appointments app
  const [page, setPage] = useState(1);
  const [tasks, setTasks] = useState([]);
  const [appointments, setAppointments] = useState([]);





  // JSX
  return (
    <div className="App">
      <div id="APP-header">
        {page===1 ? "Task Management App" :
         page===2 ? "Appointment Management App" : 
         "Error"}
      </div>
      <div id="App-body">
        {page === 1 ? <TaskManagement tasks={tasks} /> : null}
        {page === 2 ? <AppointManagement appointments={appointments} /> : null}
      </div>
    </div>
  );
}

export default App;
