import React, { useState } from 'react';



export default function Confirmation(props) {
  // Later modify this module with useContext so that this can be re-used by other purpose then logout confirmation!!!

  const { setConfirmation } = props;
  const [logoutChk, setLogoutChk] = useState(true);
  const [menuChk, setMenuChk] = useState(false);


  function handleLogoutClick(e) {
    e.preventDefault();
    setConfirmation(false);
  }

  function handleNotLogoutClick(e) {
    e.preventDefault();
    setLogoutChk(false);
    setMenuChk(true);
  }

  // JSX
  return (
    <div id="confirmation-body">
      {!logoutChk ? "" :
        <div id="check-contents">
          <h2>Are you sure to log out from this App?</h2>
          <button onClick={handleLogoutClick}>YES</button>
          <button onClick={handleNotLogoutClick}>NO</button>
        </div>
      }
      {!menuChk ? "" : 
        <div id="check-contents">
          <h2>Do you want to go back to App Menu Page?</h2>
          <button>YES</button>
          <button>NO</button>
        </div>
      }
    </div>
  );
}
