import React, { useState } from 'react';


// Function: Ask user if he/she wants to 1.logout, 2.move back to menu, or 3.stay in the app
// pre: receive "setConfirmation" switch to display setConfirmation modal or not
// post: return "setConfirmation", and answer for the next action
export default function Confirmation(props) {
  // Later modify this module with useContext so that this can be re-used by other purpose then logout confirmation!!!

  const { setConfirmation } = props;

  const [logoutChk, setLogoutChk] = useState(true);
  const [menuChk, setMenuChk] = useState(false);


  function handleLogoutClick(e) {
    e.preventDefault();
    setConfirmation(true);
  }

  function handleNotLogoutClick(e) {
    e.preventDefault();
    setLogoutChk(false);
    setMenuChk(true);
  }

  function handleMoveToMenuClick(e) {
    e.preventDefault();
    // set back to the initial states
    setLogoutChk(true);
    setMenuChk(false);
    window.location.href="http://localhost:3111/menu";
  }

  function handleStayClick(e) {
    e.preventDefault();
  
  }

  // JSX
  return (
    <div id="confirmation-body">
      {!logoutChk ? "" :
        <div id="check-contents">
          <h2>Are you sure to log out from this App?</h2>
          <button onClick={handleLogoutClick}>YES</button>
          <button onClick={handleNotLogoutClick}>NO</button>
        </div>
      }
      {!menuChk ? "" : 
        <div id="check-contents">
          <h2>Do you want to go back to App Menu Page?</h2>
          <button>YES</button>
          <button>NO</button>
        </div>
      }
    </div>
  );
}
