import React from 'react';



export default function TaskManagement(props) {
  const {tasks, handleNewTaskChange} = props;


  // JSX
  return (
    <div id="task-management-container">

      <form onSubmit={props.addTask}>
        <label>Task Title: </label>
        <input type='text' name="title" placeholder="Add a task title" />
        <br />
        <label>Task Details: </label>
        <input type="text" name="details" placeholder="Add a task details" />
        <br />
        <button type="submit">Add Task</button>
      </form>

      <ul>
        {props.tasks.map((task, index) => (
          <li key={index}>{task}</li>
        ))}
      </ul>

      {/* For now disable those functions below */}
      <div id="taskmgt-btn-container">
        <button onClick={props.clearTasks}>Clear Tasks</button>
        <button onClick={props.deleteAllTasks}>Delete All Tasks</button>
        <button onClick={props.toggleCompletedTasks}>Toggle Completed Tasks</button>
        <button onClick={props.sortTasks}>Sort Tasks</button>
        <button onClick={props.filterTasks}>Filter Tasks</button>
        <button onClick={props.searchTasks}>Search Tasks</button>
        <button onClick={props.undoLastTask}>Undo Last Task</button>
      </div>

    </div>
  );
}
