import React from 'react';
import CalendarSection from './CalendarSection';


export default function AppointManagement(props) {
  const { appointments, handleNewAppointChange, handleAppointSubmit, handleAppointDelete } = props;

  // JSX
  return (
    <div id="appoint-mgt-container">
      <div id="appoint-mgt-msg-container">
        <p>{props.appointments.length} appointments scheduled for today.</p>
        {/* <p>Click on an appointment to view or edit details.</p> */}
      </div>

      {/* Render appointments */}
      <div id="appoint-list-container">
        {props.appointments.map((appointment, index) => (
          <div key={index} className="appointment-card">
            <h2>{appointment.name}</h2>
            <p>{appointment.date}</p>
            <p>{appointment.time}</p>
            <button onClick={() => handleAppointDelete(appointment.id)}>Delete</button>
          </div>
        ))}
      </div>

      <form id="appoint-input-form" onSubmit={handleAppointSubmit}>
        <input type="text" name="name" placeholder="Requester Name" onChange={handleNewAppointChange} /><br/>
        <input type="text" name="title" placeholder="Appointment Title" onChange={handleNewAppointChange} /><br/>
        <input type="date" name="date" onChange={handleNewAppointChange} /><br/>
        <button onClick={props.handleAddAppointment}>Add Appointment</button>
      </form>
      <hr />

      <CalendarSection appoints={appointments}/>
    </div>
  );
}
